import React, { useState, useEffect } from "react";
import "./App.css";
import NavBar from "./components/common/navbar/NavBar";
import Head from "./components/common/header/Head";
import Footer from "./components/common/footer/Footer";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

function App() {
  const [isScrolled, setIsScrolled] = useState(false);

  // Add a scroll event listener to track when the user scrolls
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 0) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    // Clean up the event listener when the component unmounts
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <>
      <Router>
        {/* Conditionally apply a CSS class when scrolled */}
        <Head className={isScrolled ? "scrolled" : ""} />
        <NavBar className={isScrolled ? "scrolled" : ""} />
        <Switch>
          {/* Add your routes here */}
        </Switch>
        <Footer />
        <Footer />
        <Footer />
      </Router>
    </>
  );
}

export default App;
