import React, { useState } from 'react';
import NavBar from '../navbar/NabBar';
import Head from '../../common/header/Head';
import Footer from '../../common/footer/Footer';
import './userinfo.css';

const UserInfo = () => {
  const initialUserInfo = {
    name: 'John Doe',
    dateOfBirth: '01/01/1990',
    gender: 'Male',
    phoneNumber: '123-456-7890',
    email: 'johndoe@example.com',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
  };

 // Create state variables for user information
 const [userInfo, setUserInfo] = useState(initialUserInfo);

 const handleInputChange = (e, field) => {
   // Update the corresponding field in the user information state
   setUserInfo({
     ...userInfo,
     [field]: e.target.value,
   });
 };

 return (
   <>
     <Head />
     <NavBar />

     <div className="user-info-container">
       <div className="user-info-column">
         <h2>User Information</h2>
         <ul>
           <li>
             <strong>Name:</strong>
             <input
               type="text"
               value={userInfo.name}
               readOnly // Initially set as read-only
               onClick={() => setUserInfo({ ...userInfo, name: '' })} // Remove readOnly on click
               onChange={(e) => handleInputChange(e, 'name')}
             />
           </li>
           <li>
             <strong>Date of Birth:</strong>
             <input
               type="text"
               value={userInfo.dateOfBirth}
               readOnly
               onClick={() => setUserInfo({ ...userInfo, dateOfBirth: '' })}
               onChange={(e) => handleInputChange(e, 'dateOfBirth')}
             />
           </li>
           <li>
             <strong>Gender:</strong>
             <input
               type="text"
               value={userInfo.gender}
               readOnly
               onClick={() => setUserInfo({ ...userInfo, gender: '' })}
               onChange={(e) => handleInputChange(e, 'gender')}
             />
           </li>
           <li>
             <strong>Phone Number:</strong>
             <input
               type="text"
               value={userInfo.phoneNumber}
               readOnly
               onClick={() => setUserInfo({ ...userInfo, phoneNumber: '' })}
               onChange={(e) => handleInputChange(e, 'phoneNumber')}
             />
           </li>
           <li>
             <strong>Email:</strong>
             <input
               type="text"
               value={userInfo.email}
               readOnly
               onClick={() => setUserInfo({ ...userInfo, email: '' })}
               onChange={(e) => handleInputChange(e, 'email')}
             />
           </li>
           <li>
             <strong>Description:</strong>
             <input
               type="text"
               value={userInfo.description}
               readOnly
               onClick={() => setUserInfo({ ...userInfo, description: '' })}
               onChange={(e) => handleInputChange(e, 'description')}
             />
           </li>
         </ul>
       </div>
     </div>

     <div className="user-actions">
       <button>Change Password</button>
     </div>

     <Footer />
   </>
 );
};

export default UserInfo;
