import React, { useState, useEffect } from 'react';
import NavBar from '../navbar/NavBar';
import Head from '../../common/header/Head';
import Footer from '../../common/footer/Footer';
import './userinfo.css';

const UserInfo = () => {
  // Sample user information, replace with actual user data
  const initialUserInfo = {
    name: 'John Doe',
    dateOfBirth: '01/01/1990',
    gender: 'Male',
    phoneNumber: '123-456-7890',
    email: 'johndoe@example.com',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
  };

  const [userInfo, setUserInfo] = useState(initialUserInfo);
  const [isEditing, setIsEditing] = useState(false);

  // Function to handle form submission when updating user information
  const handleUpdate = (e) => {
    e.preventDefault();
    // Implement your logic to update user information here
    // You can send a request to your server or update the state
    // For now, we'll just toggle the editing state
    setIsEditing(false);
  };

  return (
    <>
      <Head />
      <NavBar />

      <div className="user-info-container">
        <div className="user-info-column">
          <h2>User Information</h2>
          <form onSubmit={handleUpdate}>
            <div className="form-group">
              <label>Name:</label>
              {isEditing ? (
                <input
                  type="text"
                  value={userInfo.name}
                  // Handle input change here
                />
              ) : (
                <span>{userInfo.name}</span>
              )}
            </div>
            {/* Add similar fields for Date of Birth, Gender, etc. */}
            {isEditing ? (
              <button type="submit">Save</button>
            ) : (
              <button onClick={() => setIsEditing(true)}>Edit</button>
            )}
          </form>
        </div>
        <div className="user-info-column">
          {/* Display the user information in the second column */}
          {/* You can use the same pattern as in the first column */}
        </div>
      </div>

      <div className="user-actions">
        <button>Change Password</button>
      </div>

      <Footer />
    </>
  );
};

export default UserInfo;
